import React, { useState, useEffect } from 'react';
import './Slideshow.css';

const slides = [
    {
        caption: 'Caption 1',
        imageUrl: 'https://i.ibb.co/WFhGnHn/coffee.jpg',
    },
    {
        caption: 'Caption 2',
        imageUrl: 'https://i.ibb.co/HxqH9FP/meeting.jpg',
    },
    {
        caption: 'Caption 3',
        imageUrl: 'https://i.ibb.co/sgL8xvQ/man-working.jpg',
    }
];

function SlideShow() {
    const [currentSlide, setCurrentSlide] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
        }, 3000); // Change every 3 seconds

        return () => clearInterval(interval);
    }, []);

    return (
        <div className="slideShow">
            <div className="caption">{slides[currentSlide].caption}</div>
            <img src={slides[currentSlide].imageUrl} alt={slides[currentSlide].caption} />
        </div>
    );
}

export default SlideShow;
